    }

    bfr := &BoundedFrequencyRunner{
        name:        name,
        fn:          fn,                               //被调用处理的func
        minInterval: minInterval,
        maxInterval: maxInterval,
        run:         make(chan struct{}, 1),
        timer:       timer,
    }
    if minInterval == 0 {                            //最小间隔时间如果不指定，将不受限制
        bfr.limiter = nullLimiter{}
    } else {
        // TokenBucketRateLimiter的实现流控机制，有兴趣可以再深入了解机制，此处不展开
